getCodeMirrorValue = do r <- getCodeMirrorValueAux
                        return (unpack r)

foreign import javascript safe
  "$r = textarea.getValue();"
  getCodeMirrorValueAux2 :: IO JSString
getCodeMirrorValue2 = do r <- getCodeMirrorValueAux2
                         return (unpack r)

