getValue i = do r <- getValueAux (pack i)
                return (unpack r)

foreign import javascript safe
  "$r = parent[$1].getValue();"
   getValueParentAux :: JSString -> IO JSString
getValueParent i = do r <- getValueParentAux (pack i)
                      return (unpack r)

