    "        function handleFileSelect(evt) { \
    \            var files = evt.target.files; \
    \            var reader = new FileReader(); \
    \              reader.onload = (function(theFile) { \
    \                return function(e) { \
    \                  myCodeMirror.setValue(e.target.result); \
    \                }; \
    \              })(files[0]); \
    \              reader.readAsText(files[0]); \
    \        }; \
