  uploadFile :: IO ()

foreign import javascript safe
    "if (window.File && window.FileReader && window.FileList && window.Blob) { $r = true; } else { $r = false; };"
  isUploadSupportedAux :: IO JSVal
isUploadSupported :: IO Bool
isUploadSupported = do b <- isUploadSupportedAux
                       return (fromJSBool b)

compile :: IO ()
compile = do setCodeMirrorValue2 "Compiling...\n\nThis could take more than one minute."
             setCodeMirror2Plain
             disableButton "compile"
             disableButton "reset"
             disableButton "submit"
             disableButton "example"
             disableButton "example2"
             disableButton "upload"
             disableButton "cancel"
             y <- getCodeMirrorValue
             x <- doCompile y
             case x of
                Just x -> do { setCodeMirrorValue2 ("Compilation error:\n\n" ++ x);
                               setCodeMirror2Plain }
                Nothing -> do { enableButton "submit" }
             enableButton "compile"
             enableButton "reset"
             enableButton "example"
             enableButton "example2"
             ies <- isUploadSupported
             if (ies) then enableButton "upload" else (return ())
             enableButton "minimise"
             enableButton "cancel"

embedCode :: [String] -> String
embedCode [] = prefix
embedCode (h:t) = (prefix ++ (h ++ "\n")) ++ (unlines (map ((take 11 $ repeat ' ') ++) t))

reset :: IO ()
reset = do x <- getValueParent "codeArea"
           setCodeMirrorValue $ embedCode $ lines x
           setCodeMirrorValue2 explanation
           setCodeMirror2Plain
           disableButton "submit"

submit :: IO ()
submit = do x <- getCodeMirrorValue2
            clearWorkspace
            setValueParent "codeArea" x
            codeToBlockly

download :: IO ()
download = do c <- getCodeMirrorValue
              downloadFile c "Main.hs" 

upload :: IO ()
upload = return ()

minimise :: IO ()
minimise = hideIFrame

cancel :: IO ()
cancel = destroyIFrame 

example :: IO ()
example = setCodeMirrorValue exampleCode

example2 :: IO ()
example2 = setCodeMirrorValue exampleCode2

main :: IO ()
main = do setOnClick "compile" compile 
          setOnClick "reset" reset 
          setOnClick "example" example
          setOnClick "example2" example2
          setOnClick "submit" submit
          setOnClick "download" download
          setOnClick "upload" uploadFile
          setOnClick "minimise" minimise
          setOnClick "cancel" cancel 
          enableButton "compile"
          enableButton "reset"
          enableButton "example"
          enableButton "example2"
          enableButton "download"
          ies <- isUploadSupported
          if (ies) then enableButton "upload" else (return ())
          enableButton "minimise"
          enableButton "cancel"
          reset

prefix = "module Main where\n\nimport Marlowe\nimport Fay.FFI (ffi)\n\nsetCode :: String -> Fay ()\nsetCode = ffi \"textarea.setValue(%1)\"\n\nmain :: Fay ()\nmain = setCode (prettyPrintContract contract)\n\n-------------------------------------\n-- Write your code below this line --\n-------------------------------------\n\n\ncontract :: Contract\ncontract = "

exampleCode = "module Main where\n\nimport Marlowe\nimport Fay.FFI (ffi)\n\nsetCode :: String -> Fay ()\nsetCode = ffi \"textarea.setValue(%1)\"\n\nmain :: Fay ()\nmain = setCode (prettyPrintContract contract)\n\n-------------------------------------\n-- Write your code below this line --\n-------------------------------------\n\n-- Escrow example using embedding\n\ncontract :: Contract\ncontract = CommitCash iCC1 alice\n                      (ConstMoney 450)\n                      10 100\n                      (When (OrObs (majority_chose refund)\n                                   (majority_chose pay))\n                            90\n                            (Choice (majority_chose pay)\n                                    (Pay iP1 alice bob\n                                         (AvailableMoney iCC1)\n                                         100\n                                         redeem_original)\n                                    redeem_original)\n                            redeem_original)\n                      Null\n    where majority_chose = two_chose alice bob carol\n\n-- Participants\n\nalice, bob, carol :: Person\nalice = 1\nbob = 2\ncarol = 3\n\n-- Possible votes \n\nrefund, pay :: ConcreteChoice\nrefund = 0\npay = 1\n\n-- Vote counting\n\nchose :: Integer -> ConcreteChoice -> Observation\nchose per c = PersonChoseThis (IdentChoice per) per c\n\none_chose :: Person -> Person -> ConcreteChoice -> Observation\none_chose per per' val = (OrObs (chose per val) (chose per' val)) \n                                  \ntwo_chose :: Person -> Person -> Person -> ConcreteChoice -> Observation\ntwo_chose p1 p2 p3 c = OrObs (AndObs (chose p1 c) (one_chose p2 p3 c))\n                             (AndObs (chose p2 c) (chose p3 c))\n\n-- Redeem alias\n\nredeem_original :: Contract\nredeem_original = RedeemCC iCC1 Null\n\n-- Commit identifier \n\niCC1 :: IdentCC\niCC1 = IdentCC 1\n\n-- Payment identifier\n\niP1 :: IdentPay\niP1 = IdentPay 1\n\n"

exampleCode2 = "module Main where\n\nimport Marlowe\nimport Fay.FFI (ffi)\n\nsetCode :: String -> Fay ()\nsetCode = ffi \"textarea.setValue(%1)\"\n\nmain :: Fay ()\nmain = setCode (prettyPrintContract contract)\n\n-------------------------------------\n-- Write your code below this line --\n-------------------------------------\n\n-- Zero Coupon Bond example using embedding\n\ncontract :: Contract\ncontract = couponBondGuaranteed 1 2 3 1000 0.08 50 100 450 30240\n\ncouponBondGuaranteed :: Integer -> Integer -> Integer -> Integer -> Double -> Timeout\n                     -> Timeout -> Timeout -> Timeout -> Contract\ncouponBondGuaranteed creatorID counterpartyID guarantor notionalPrincipal nominalInterestRate initialExchangeDate slotCycle maturityDate gracePeriod =\n    -- counterpartyID commits a bond face value before initialExchangeDate\n    CommitCash (IdentCC 0) counterpartyID (ConstMoney notionalPrincipal) initialExchangeDate maturityDate\n        -- guarantor commits a 'guarantee' before initialExchangeDate\n        (CommitCash (IdentCC 1) guarantor (ConstMoney totalPayment) initialExchangeDate (maturityDate + gracePeriod)\n            (Both\n                -- creatorID can receive the payment from counterpartyID\n                (Pay (IdentPay 1) counterpartyID creatorID (AvailableMoney (IdentCC 0)) maturityDate Null)\n                -- schedule payments\n                (Both payments finalPayment)\n            )\n            -- if no guarantee committed we abort contract and allow to redeem the counterpartyID's commit\n            (RedeemCC (IdentCC 0) Null)\n        )\n        Null\n  where\n    cycles = takeWhile (\\i ->\n            let paymentDate = initialExchangeDate + i * slotCycle\n            in paymentDate < maturityDate\n        ) [1..]\n\n    -- calculate payment schedule\n    paymentDates = map (\\i -> initialExchangeDate + i * slotCycle) cycles\n\n    coupon = round $ fromIntegral notionalPrincipal * nominalInterestRate\n\n    -- calculate total amount of payments to be ensured by guarantor\n    totalPayment = notionalPrincipal + coupon * length cycles\n\n    -- generate Commit/Pay for each scheduled payment\n    payment amount (ident, paymentDate) =\n        -- creatorID commits a coupon payment\n        CommitCash (IdentCC ident) creatorID (ConstMoney amount) paymentDate (maturityDate + gracePeriod)\n            (When FalseObs paymentDate Null\n                -- counterpartyID can claim the coupon after payment date\n                (Pay (IdentPay ident) creatorID counterpartyID (AvailableMoney (IdentCC ident)) (maturityDate + gracePeriod) Null))\n            -- in case creatorID did not commit on time the guarantor pays the coupon\n            (Pay (IdentPay (ident + 1)) guarantor counterpartyID (ConstMoney amount) (maturityDate + gracePeriod) Null)\n\n    -- generate coupon payments for given schedule\n    payments = foldr1 Both $ map (payment coupon) idsAndDates\n        -- generate IdentCC/IdentPay identifiers for each payment date\n        where idsAndDates = zip (map (2*) [1..]) paymentDates\n\n    finalPayment = payment notionalPrincipal (2 * (1 + length paymentDates), maturityDate)\n"

explanation = "Embedded Fay code editor\n========================\n\nYou can use this editor to generate Marlowe code (to use with Meadow) by using Marlowe's Fay embedding.\n\nFay is a subset of Haskell (https://github.com/faylang/fay/wiki)\n\nThe editor includes a pruned version of the Fay compiler. You can control the editor and the compiler by using the buttons at the bottom:\n\n* Execute Fay code - compiles and runs the Fay code on the left panel and writes the output to this panel.\n\n* Import Meadow contract to Fay code - creates a template based on the Marlowe contract in Meadow (under the editor) and shows this message. If you have just opened the Fay editor the code in Meadow has already been imported.\n\n* Load escrow example - loads, in the left panel, an example of how the Fay embedding of Marlowe can be used to implement an escrow contract.\n\n* Load coupon bond guaranteed example - loads, in the left panel, an example of how the Fay embedding of Marlowe can be used to implement a safe coupon bond guaranteed contract.\n\n* Export generated contract to Meadow - once you have successfully generated a Marlowe contract from the Fay code by clicking the \"Execute Fay code\" button, this button will allow you to copy the generated contract (which will appear on this panel) back to Marlowe (under this editor).\n\n* Load Fay code from file - allows you to select a \".hs\" file from your computer that will be displayed on the left panel.\n\n* Save Fay code to file - allows you to save the code on the left panel to a \".hs\" file in your computer.\n\n* Minimise Fay editor - will hide the Fay editor so that you can keep using Meadow, but it will not delete its contents. If Fay code is being executed or compiled when you minimise the editor, the process will continue in the background.\n\n* Close Fay editor - it closes the editor. WARNING: if you close the editor or navigate away from this page, the contents of the editor will be lost. Make sure you save them to a file in your computer before that.\n\nPlease note that the compiler included does not do type-checking of the code provided. This is because the Fay compiler relies on GHC for that, and this demo runs in the browser (so it does not have access to GHC). You can use GHC offline in order to type-check your programs, the source for the Marlowe module can be found in the path \"/meadow/editor/fay-code\" of the Github repository.\n\n\n"

